<?php
// phpinfo();


class Movie{
    public $title;
    public $img;
    public $genre;
    public $year;
    public $language;
    public $voto;
    

    function __construct($_title){
        $this->title = $_title;
    }
    public function getImg($_path){
        $this->img = $_path;
    }
    public function generaVoto(){
        if($this->voto == 0){
            return $this->voto = rand(1,5);
        }
    }
    
}




// first movie
$firstMovie = new Movie('Avatar');
$firstMovie->genre = 'Fantasy';
$firstMovie->year = '2009';
$firstMovie->language = 'EN';
$firstMovie->generaVoto();
$firstMovie->getImg('https://media.gqitalia.it/photos/5df8f7fd6402f60008d6e28d/1:1/w_1973,h_1973,c_limit/IPA_IPA908549.jpg');
// second movie
$secondMovie = new Movie('Top Gun Maverick');
$secondMovie->genre = 'action';
$secondMovie->year = '2022';
$secondMovie->language = 'EN';
$secondMovie->generaVoto();
$secondMovie->getImg('data:image/jpeg;base64,');


?>


